mapper.select:
SELECT * FROM (
	SELECT rownum r_num,a.* FROM(
		SELECT * FROM QNABOARD
		ORDER BY qb_groups DESC, qb_lev ASC 
	) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE QNABOARD
SET qb_subject = #{qb_subject}, 
	qb_content = #{qb_content}
WHERE qb_num = #{qb_num}

update QNABOARD
set qb_readcount = qb_readcount+1
where qb_num = #{qb_num}
