mapper.select:
	SELECT * FROM (	SELECT rownum as r_num, a.* FROM (
			SELECT * FROM bbs2
			WHERE bname=#{bname}
			ORDER BY groups DESC, lev ASC
		) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE bbs2
SET lev=lev+1
WHERE groups=#{groups} 
AND
	lev > #{lev}

UPDATE bbs2
SET title=#{title},
	content=#{content}
	
		,uploadFileName=#{uploadFileName}
	
WHERE seq=#{seq} AND pwd=#{pwd}

UPDATE bbs2
SET hit = hit+1
WHERE seq=#{no}
