mapper.select:
	SELECT * FROM (
		SELECT rownum as r_num, a.* FROM (
			SELECT b_idx,send_tel,
	 request_tel,content,to_char(write_date,'yy-mm-dd')write_date,status,subject FROM msg_t where status = 0 and send_tel like #{send_tel}
			) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT b_idx,send_tel,
 request_tel,content,to_char(write_date,'yy-mm-dd')write_date,status,subject FROM msg_t
WHERE b_idx=#{n}

mapper.update:
update msg_t
set status = 1
where b_idx=#{b_idx}
