mapper.select:
SELECT * FROM (
	SELECT rownum r_num,a.* FROM(
		SELECT * FROM matchboard
		ORDER BY mb_num DESC 
	) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE matchboard
SET mb_subject = #{mb_subject}, 
	mb_content = #{mb_content},
	mb_city = #{mb_city}, 
	mb_state = #{mb_state}
WHERE mb_num = #{mb_num}
