mapper.select:
	SELECT * FROM (
		SELECT rownum as r_num, a.* FROM (
			SELECT * FROM silver_notice where status = 0
			) a
	) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE silver_notice
SET subject=#{subject},
	content=#{content}
WHERE b_idx=#{b_idx}

update silver_notice
set status = 1
where b_idx=#{b_idx} and nickname=#{nickname}
